mapper.select:
SELECT * FROM( SELECT A.*, ROWNUM RNUM FROM( SELECT MAT_NO, MAT_TIT, MAT_NM, TO_CHAR(MAT_DATE, 'YYYY-MM-DD') MAT_DATE FROM MATTERS
ORDER BY ROWID DESC )A )B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.insert:

	SELECT MAT_SEQ.nextval
	FROM DUAL

INSERT INTO MATTERS
(
	MAT_NO
	,	MAT_TIT
	,	MAT_NM
	,	MAT_PASS
	,	MAT_CON
	,	TO_CHAR(MAT_DATE, 'YYYY-MM-DD') MAT_DATE
)VALUES(
	#{matNo, jdbcType=NUMERIC}
,	#{matTit, jdbcType=VARCHAR}
,	#{matNm, jdbcType=VARCHAR}
,	#{matPass, jdbcType=VARCHAR}
,	#{matCon, jdbcType=VARCHAR}
,	SYSDATE
)

mapper.update:
UPDATE MATTERS
SET
	MAT_TIT = #{matTit, jdbcType=VARCHAR}
,	MAT_NM = #{matNm, jdbcType=VARCHAR}
,	MAT_CON = #{matCon, jdbcType=VARCHAR}
WHERE MAT_NO = #{matNo}
